import React, { createContext, useState, useEffect } from 'react'
import { useNavigate } from 'react-router-dom'
import { LoginAPI } from '../Config/Api';
import axios from 'axios';

export const AuthContext = createContext();

export const AuthProvider =  ({children}) => {
    const navigate = useNavigate();
    const [user, SetUser] = useState(null);
    const [loading, setLoading] = useState(true);
    const [perm, setPerm] = useState("");

    useEffect(() => {
        const recoveredUser = localStorage.getItem('user');

        if(recoveredUser){
            SetUser(JSON.parse(recoveredUser));
        }

        setLoading(false);
    },[]);
    
    const LoginUser = async (cliente, login, password) => {
        console.log("login", {cliente, login, password});
        
        const { data } = await axios.get(LoginAPI(cliente, login, password));
        setPerm(data);
        console.log("permisssion", perm);
      

        const loggedUser = {
            id:'1',
        }

        localStorage.setItem("user", JSON.stringify(loggedUser));

        if(password==="123"){
            SetUser({loggedUser});	
            navigate("/");
        }
    };

    const logoutUser = () => {
        console.log("logout");
        SetUser(null);
        localStorage.removeItem("user");
        navigate("/login")
    };

    return(
        <AuthContext.Provider value={{authenticated:!!user, user, loading, LoginUser, logoutUser}}>
            {children}
        </AuthContext.Provider>
    )
}
