import React, { useState, useContext} from 'react'
import { AuthContext } from '../../Context/auth';
import AccountCircleIcon from '@mui/icons-material/AccountCircle';
import BusinessIcon from '@mui/icons-material/Business';
import VpnKeyIcon from '@mui/icons-material/VpnKey';

const LoginPage = () => {

  const { authenticated, loginUser } = useContext(AuthContext);

  const [cliente, setCliente] = useState('');
  const [login, setLogin] = useState('');
  const [password, setPassword] = useState('');

  const HandleSubmit = (e) =>{
    e.preventDefault();
    console.log("submit", {cliente, login, password});
    loginUser(cliente, login, password);
  };

  return (
    <form action="#" className="sign-in-form">
    <h2 className="title">Fazer Login</h2>
    <div className="input-field">
    <i><BusinessIcon/></i>
    <input type="text" placeholder="Empresa" 
    onChange={(e)=>envio(e)} 
    id='cliente'
    value={data.cliente}
    />
    </div>
    <div className="input-field">
    <i><AccountCircleIcon/></i>
    <input type="text" placeholder="Login" 
     onChange={(e)=>envio(e)} 
     id='login'
     value={data.login}
    />
    </div>
    <div className="input-field">
    <i><VpnKeyIcon/></i>
    <input type="password" placeholder="Senha" 
      onChange={(e)=>envio(e)} 
      id='password'
      value={data.password}
    />
    </div>
    <input type="submit" value="Entrar" className="botao solid" 
    onClick={loginUser}
    />
</form>
  )
}

export default LoginPage
