import React from 'react'
import HomeIcon from '@mui/icons-material/Home';

class MainNav extends React.Component {

    constructor(props) {
        super(props)
        this.state = {
            tab: "Home"
        }
    }


    render(){
        return(
            <div className='mainnav'>
                <ul className="nav-menu custom-scrollbar">
                    <div className={this.state.tab === "Home" ? "active" : ""}>
                        <li className="dropdown">
                            <a  className="nav-link menu-title" href={`/`} 
                            onClick={() => this.setState({tab: "Home"})}
                            >
                                <i><HomeIcon/></i>
                                <span>Home</span>
                            </a>
                        </li>
                    </div>
                    <div className={this.state.tab === "Campanhas" ? "active" : ""}>
                        <li className="dropdown">
                            <a  className="nav-link menu-title" href={`/`} 
                            onClick={() => this.setState({tab: "Campanhas"})}
                            >
                                <i><HomeIcon/></i>
                                <span>Campanhas</span>
                            </a>
                        </li>
                    </div>
                    <div className={this.state.tab === "Configurações" ? "active" : ""}>
                        <li className="dropdown">
                            <a  className="nav-link menu-title" href={`/`} 
                            onClick={() => this.setState({tab: "Configurações"})}
                            >
                                <i><HomeIcon/></i>
                                <span>Configurações</span>
                            </a>
                        </li>
                    </div>
                </ul>
            </div>
        )
    }
  
}

export default MainNav
