    const usuario = localStorage.getItem("user")

    console.log("usuario area: ",usuario.nome);

        
    
  return (
    <div className="sidebar-user text-center"><a className="setting-primary"><i data-feather="settings"></i></a><img className="img-90 rounded-circle" src={dashboard1} alt=""/>
        <div className="badge-bottom"><span className="badge badge-primary">Webck</span></div><a href="user-profile.html">
        <h6 className="mt-3 f-14 f-w-600">{usuario?.nome}</h6></a>
        <p className="mb-0 font-roboto">Admin</p>
    </div>
  )
}

