import React, { createContext, useState } from 'react'
import { useNavigate } from 'react-router-dom'

export const AuthContext = createContext();

export const AuthProvider =  ({children}) => {
    const navigate = useNavigate();
    const [user, SetUser] = useState(null);

    
    const loginUser = (cliente, login, password) => {
        console.log("login", {cliente, login, password});
        if(password==="123"){
            SetUser({id:"1", codigo: "1"});	
            navigate("/");
        }
    };

    const logoutUser = () => {
        console.log("logout")
    };


    return(
        <AuthContext.Provider value={{authenticated:!!user, user, loginUser, logoutUser}}>
            {children}
        </AuthContext.Provider>
    )
}
