import axios from 'axios';
import React, { useState, useEffect, useContext } from 'react';
import { consultarToken } from '../Config/Api'
import { AuthContext } from '../Context/auth';
import Tabela from './Tabela';


const TokenTable = () => {
    const { user } = useContext(AuthContext);
    const [dia, setDia] = useState("");
    console.log("area do TOKEN TABLE", user?.idEmpresa);

    const buscarTabela = async () => {
        const { data } = await axios.get(consultarToken(user?.idEmpresa));
        setDia(data);
      };

      useEffect(()=> {
        buscarTabela();
      },[])
     
      console.log(data.length)

      const column = [
        {heading: "Token", value: 'token'},
        {heading: "Descrição", value: 'descricao'},
        {heading: "Status", value: "status"},
        {heading: "Opções", value: <div>
             <a href="#" class="btn btn-primary shadow btn-xs sharp"><i class="fa fa-pencil"><div class="fechado"><span class="tooltiptext">QR CODE</span></div></i></a>
            <a class="btn btn-danger shadow btn-xs sharp mr-1"><i class="fa fa-trash"><div class="fechado"><span class="tooltiptext">Excluir Vencimento</span></div></i></a>
        </div>},
      ]
  return (
    <div className="table-responsive">
        <Tabela data={dia} column={column}/>
    </div>
  )
}

export default TokenTable
