import React, { createContext, useState, useEffect } from 'react'
import { useNavigate } from 'react-router-dom'
import { LoginAPI } from '../Config/Api';
import axios from 'axios';
import { createSession } from '../Config/services';

export const AuthContext = createContext();

export const AuthProvider =  ({children}) => {
    const navigate = useNavigate();
    const [user, SetUser] = useState(null);
    const [loading, setLoading] = useState(true);
    const [perm, setPerm] = useState("");

    useEffect(() => {
        const recoveredUser = localStorage.getItem('user');

        if(recoveredUser){
            SetUser(JSON.parse(recoveredUser));
        }

        setLoading(false);
    },[]);
    
    const loginUser = async (cliente, login, password) => {
        const response = await createSession(cliente, login, password);
        console.log("login", response.data);
        

        const loggedUser = response.data.id

        localStorage.setItem("user", JSON.stringify(loggedUser));

        if(response.data.codigo==="1"){
            SetUser({loggedUser});	
            navigate("/");
        }else{
            return  <h1>Cliente login ou senha inv√°lidos</h1>
        }
        
    };

    const logoutUser = () => {
        console.log("logout");
        SetUser(null);
        localStorage.removeItem("user");
        navigate("/login")
    };

    return(
        <AuthContext.Provider value={{authenticated:!!user, user, loading, loginUser, logoutUser}}>
            {children}
        </AuthContext.Provider>
    )
}
