import React, {useState, useEffect} from 'react'
import { consultarToken } from '../Config/Api'
import axios from 'axios'

const TokenTable = () => {
    const[lista, setLista]= useState('');
    const [loading, setLoading] = useState(true);
    const [idToken, setIdToken] = useState('');
    const idEmpresa = idToken?.idEmpresa;

    useEffect(() => {
        const recoveredUser = localStorage.getItem('user');

        if(recoveredUser){
            setIdToken(JSON.parse(recoveredUser));
        }
        setLoading(false);
    },[buscarToken]);

    const buscarToken = async () => {
        if(idEmpresa!= ''){
            const { data } = await axios.get(consultarToken(idEmpresa));
            setLista(data);
        }else{
            return <h1>ERROR!</h1>
        }

    }

    console.log("Tabela ID: ", idEmpresa);
    console.log("lista da tabela: ", lista)

