import axios from 'axios';
import React, { useState, useEffect, useContext } from 'react';
import { consultarToken } from '../Config/Api'
import { AuthContext } from '../Context/auth';
import Tabela from './Tabela';


const TokenTable = () => {
    const [posts, setPosts] = useState({ blogs: []})
    const { user } = useContext(AuthContext);


      useEffect(()=> {
        const buscarTabela = async () => {
          const { data } = await axios.get(consultarToken(user?.idEmpresa));
          setPosts({blogs: data});
          console.log(data);
        };
        buscarTabela();
      },[setPosts])
     
 

  return (
    <div className="table-responsive">
       <table class="table table-xl">
          <thead>
            <tr>
              <th>Token</th>
              <th>Descrição</th>
              <th>Situação</th>
              <th>Opções</th>
            </tr>
          </thead>
          <tbody>
            {
              posts.blogs && posts.blogs.map((item) => (
                <tr key={item.token}>
                  <td>{item.token}</td>
                  <td>{item.descricao}</td>
                  <td>{item.status}</td>
                  <td>
                      <div class="d-flex">  
                        <a href="#" class="btn btn-primary shadow btn-xs sharp"><i class="fa fa-pencil"><div class="fechado"><span class="tooltiptext">Editar Vencimento</span></div></i></a>
                        <a class="btn btn-danger shadow btn-xs sharp mr-1"><i class="fa fa-trash"><div class="fechado"><span class="tooltiptext">Excluir Vencimento</span></div></i></a>
                      </div>
                  </td>
                </tr>
              ))
            }
          </tbody>
       </table>
    </div>
  )
}

export default TokenTable
