import axios from 'axios';
import React, {useState, useEffect, useContext} from 'react'
import { sendToken } from '../Config/Api'
import { AuthContext } from '../Context/auth';

const TokenCadastro = () => {

    const [descricao, setDescricao] = useState('');
    const [token, setToken] = useState('');
    const [loading, setLoading] = useState(true);
    const [idToken, setIdToken] = useState('');
    const [resposta, setResposta] = useState([]);
    const { enviarToken } = useContext(AuthContext);
    const idEmpresa = idToken?.idEmpresa;
    

    const submitToken = (e) => {
        setIdEmpresa(idToken)
        e.preventDefault();
        console.log("envio de token", {token, descricao,})
        sendToken(token, descricao, idEmpresa);
    }

    function uuid() {

        function randomDigit() {
    
            if (crypto && crypto.getRandomValues) {
    
                var rands = new Uint8Array(1);
    
                crypto.getRandomValues(rands);
    
                return (rands[0] % 16).toString(16);
            } else {
                return ((Math.random() * 16) | 0).toString(16);
            }
        }
        var crypto = window.crypto || window.msCrypto;
    
        var ret = 'MeZaprxxxxxxxxx4xxx8xxxxxxx'.replace(/x/g, randomDigit);
        setToken(ret);
    }

    useEffect(() => {
        const recoveredUser = localStorage.getItem('user');

        if(recoveredUser){
            setIdToken(JSON.parse(recoveredUser));
        }
        setLoading(false);
        uuid();

    },[]);

    console.log("dados token cadastro: ",token, descricao, idEmpresa);

  return (
    <div>
        <h1 className='token-tit'>Cadastrar Token</h1>
        <label class="form-label">ID Empresa</label>
        <input type='text' 
        name='token' 
        id='token'
        className="form-control"
        value={idToken?.idEmpresa}
        disabled
        onChange={(e)=> setIdEmpresa(e.target.value)}
        />
        <label class="form-label">Token</label>
        <input type='text' 
        name='token' 
        id='token'
        className="form-control"
        placeholder={token}
        value={token}
        disabled
        />
        <br/>
        <label class="form-label">Descrição do Plano</label>
        <input type='text' 
        name='descricao' 
        id='descricao'
        className="form-control"
        placeholder='Digite a descricao do Token'
        value={descricao}
        onChange={(e)=> setDescricao(e.target.value)}
        />
        <br/>
        <div className='enviarbotao'>
            <a class="btn btn-primary" onClickCapture={submitToken}>Cadastrar Token</a>
        </div>
    </div>
  )
}

export default TokenCadastro
