import React from 'react'
import HomeIcon from '@mui/icons-material/Home';
import BusinessCenterIcon from '@mui/icons-material/BusinessCenter';
import SettingsSuggestIcon from '@mui/icons-material/SettingsSuggest';

class MainNav extends React.Component {

    constructor(props) {
        super(props)
        this.state = {
            tab: "Home"
        }
    }


    render(){
        return(
            <div className='mainnav'>
                <ul className="nav-menu custom-scrollbar">
                    <div className={this.state.tab === "Home" ? "active" : "notactive"}>
                        <li className="dropdown">
                            <a  className="nav-link menu-title"  
                            onClick={() => this.setState({tab: "Home"})}
                            >
                                <i><HomeIcon/></i>
                                <span>Home</span>
                            </a>
                        </li>
                    </div>
                    <div>
                        <li className="dropdown">
                            <a  className={this.state.tab === "Campanhas" ? "active" : "notactive"}  
                            onClick={() => this.setState({tab: "Campanhas"})}
                            >
                                <i><BusinessCenterIcon/></i>
                                <span>Campanhas</span>
                            </a>
                        </li>
                        <li className='nav-item'>
                            <h6>Minhas Campanhas</h6>
                            <h6>Cadastrar campanha</h6>
                        </li>
                    </div>
                    <div className={this.state.tab === "Configurações" ? "active" : "notactive"}>
                        <li className="dropdown">
                            <a  className="nav-link menu-title" 
                            onClick={() => this.setState({tab: "Configurações"})}
                            >
                                <i><SettingsSuggestIcon/></i>
                                <span>Configurações</span>
                            </a>
                        </li>
                    </div>
                </ul>
            </div>
        )
    }
  
}

export default MainNav
