import axios from 'axios';
import React, { useState, useEffect, useContext } from 'react';
import { consultarToken } from '../Config/Api'
import { AuthContext } from '../Context/auth';
import {
    Container,
    TableCell,
    LinearProgress,
    Typography,
    TableBody,
    TableRow,
    TableHead,
    TableContainer,
    Table,
    Paper,
    Pagination
  } from '@mui/material';


const TokenTable = () => {
    const { user } = useContext(AuthContext);
    const [tabela, setTabela] = useState();
    console.log("area do TOKEN TABLE", user?.idEmpresa);

    const buscarTabela = async () => {
        const { data } = await axios.get(consultarToken(user?.idEmpresa));
        setTabela(data);
      };

      useEffect(()=> {
        buscarTabela();
      })
   
      console.log(tabela);

  return (
    <table className="table table-responsive-md">
        <Container style={{ textAlign: "center", marginTop: 80 }} id='lista'>
            <Typography
            variant="h5"
            style={{ margin: 18, fontFamily: "Montserrat" }}
            >
            </Typography>
            <TableContainer component={Paper}
            style={{cursor: 'pointer'}}
            >
            {loading ? (
                <LinearProgress style={{ backgroundColor: "#303841" }} />
            ) : (
                <Table aria-label="simple table">
                <TableHead style={{
                    backgroundColor: "#303841",
                    border: "1px solid #FFF",
                    }}>
                    <TableRow>
                    {["Moeda", "Valor", "Atualização(24H)", "Capitalização de mercado"].map((head) => (
                        <TableCell
                        style={{
                            color: "#FFF",
                            fontSize: "17px",
                            fontWeight: "600",
                            fontFamily: "Mukta",
                            textTransform: "uppercase",
                        }}
                        key={head}
                        align={head === "Moeda" ? "" : "right"}
                        >
                        {head}
                        </TableCell>
                    ))}
                    </TableRow>
                </TableHead>

                <TableBody>
                    {buscaManual()
                    .slice((page - 1) * 10, (page - 1) * 10 + 10)
                    .map((row) => {
                        const profit = row.price_change_percentage_24h > 0;
                        return (
                        <TableRow
                            className={row}
                            key={row.name}
                            onClick={() => (navigate(`/moeda/${row.id}`), window.scroll(0,0))}
                        >
                            <TableCell
                            component="th"
                            scope="row"
                            style={{
                                display: "flex",
                                gap: 15,
                            }}
                            >
                            <img
                                src={row?.image}
                                alt={row.name}
                                height="50"
                            />
                            <div
                                style={{ display: "flex", flexDirection: "column" }}
                            >
                                <span
                                style={{
                                    textTransform: "uppercase",
                                    fontWeight: "600",
                                    fontSize: 22,
                                }}
                                >
                                {row.symbol}
                                </span>
                                <span style={{ color: "darkgrey" }}>
                                {row.name}
                                </span>
                            </div>
                            </TableCell>
                            <TableCell align="right">
                            {symbol}{" "}
                            {numberWithCommas(row.current_price.toFixed(2))}
                            </TableCell>
                            <TableCell
                            align="right"
                            style={{
                                color: profit > 0 ? "rgb(14, 203, 129)" : "red",
                                fontWeight: 500,
                            }}
                            >
                            {profit && "+"}
                            {row.price_change_percentage_24h.toFixed(2)}%
                            </TableCell>
                            <TableCell align="right">
                            {symbol}{" "}
                            {numberWithCommas(
                                row.market_cap.toString().slice(0, -6)
                            )}
                            M
                            </TableCell>
                        </TableRow>
                        );
                    })}
                </TableBody>
                </Table>
            )}
            </TableContainer>
            <Pagination
            count={(buscaManual()?.length / 10).toFixed(0)}
            style={{
                padding: 20,
                width: "100%",
                display: "flex",
                justifyContent: "center",
            }}
            onChange={(_, value) => {
                setPage(value);
            }}
            />
        </Container>
    </table>
  )
}

export default TokenTable
